     default_type &#x27;text/plain&#x27;;
     content_by_lua &#x27;local tail = io.popen(&quot;tail -n 20 &quot;..&quot;/chroot/twtxt/masukomi.txt&quot;, &quot;r&quot;)
     for line in tail:lines() do
       print(c, line)
     end
